        if (file) {
          var reader = new FileReader(); // Set the image once loaded into file reader

          reader.onload = function (e) {
            var img = document.createElement("img");
            img.id = 'result';
            img.src = e.target.result;
            var canvas = document.createElement("canvas");
            var ctx = canvas.getContext("2d");
            ctx.drawImage(img, 0, 0);
            var MAX_WIDTH = 1024;
            var MAX_HEIGHT = 768;
            var width = img.width;
            var height = img.height;

            if (width > height) {
              if (width > MAX_WIDTH) {
                height *= MAX_WIDTH / width;
                width = MAX_WIDTH;
              }
            } else {
              if (height > MAX_HEIGHT) {
                width *= MAX_HEIGHT / height;
                height = MAX_HEIGHT;
              }
            }

            canvas.width = width;
            canvas.height = height; // var ctx = canvas.getContext("2d");

            ctx.drawImage(img, 0, 0, width, height);
            var dataurl = canvas.toDataURL(file.type); //document.getElementById('lastIMG').src = dataurl;

            post.image = dataurl;
            post.lele = dataurl.length;
            console.log('readerfile new> ', post);
          };

          reader.readAsDataURL(file);
        }
      } else {
        alert('The File APIs are not fully supported in this browser.');
      }
